import React from 'react';



class Details extends React.Component{
    constructor(props){
        super(props);
        this.state={
            info:[]
        }
    }

    user = ( { match } ) => match.params.index;
    componentDidMount() {

        fetch("https://jsonplaceholder.typicode.com/comments?postId=" + user)
        .then((response) => response.json())
        .then((result) => this.setState( { info: result.slice(0,10) } ))
        .then((result) => console.log(result))
    }
    render(){
        return(
            <div>
                <h1>title: {this.props.title}</h1>
                {console.log(this.props)}
                <h2>body: {this.props.body}</h2>
                {/* <h3>{this.state.index}</h3> */}
                <ul>
                    {this.state.info.map((comment, index) => <li key={index}>{comment.name}</li>)}
                </ul>
                {console.log(this.state.info)}
                {console.log(user)}
            </div>
        )
    }
}
export default Details;
