import React from 'react';
import { Link } from "react-router-dom";

function PostCard(props) {

    getFunction = () => {
        props.updateState(props.info.title, props.info.body);
    }

    return (
        <Link to={`/posts/${props.info.id}`} onClick={ this.getFunction }>
            <div>
                <p>{props.info.title}</p>
            </div>
        </Link>
    )
}

export default PostCard
